Q10:
task(async (event) => {
  showGoldWindow(true)
  const money = event.args[0]
  let select = await talkWithArgs({
    characterName: '*',
    text: 'Q10_0',
    select: true,
    textArgs: [['{money}', money]],
  })
  while (select) {
    if (money > hero().gold) {
      await talk('*', 'NotEnoughMoney')
      break
    }
    hero().gold -= money
    select = await talk('*', 'Q10_1')
  }
  await talk('*', 'Q10_2')
  showGoldWindow(false)
})

Q4:
task(async (event) => {
  showGoldWindow(true)
  const money = event.args[0]
  await talk('*', 'Q4_0')
  const select = await talkWithArgs({
    characterName: '*',
    text: 'Q4_1',
    select: true,
    textArgs: [['{money}', money]],
  })
  if (!select) {
    await talk('*', 'Q4_N')
  } else {
    const gold = hero().gold
    if (gold < money) {
      await talk('*', 'Q4_N_MONEY')
      await talk('*', 'Q4_N')
    } else {
      hero().gold -= money
      healHP()
      const audios = executingEngine.audios
      audios.pauseBGM()
      await fading({ type: 'in', duration: 1000 })
      audios.playME('se/inn.mp3')
      await delay(3000)
      await fading({ type: 'out', duration: 1000 })
      audios.replayBGM()
      await talk('*', 'Q4_Y1')
    }
  }
  showGoldWindow(false)
})
